import React, { useState } from "react";

import "./ExpensesFilter.css";

const ExpensesFilter = () => {
  const [enterdYear1, setEnteredYear1] = useState("2022");
  const [enterdYear2, setEnteredYear2] = useState("2021");
  const [enterdYear3, setEnteredYea3] = useState("2020");
  const [enterdYear4, setEnteredYea4] = useState("2019");
  // const [newOption, useNewOption] = useState("");

  const onChangeHandlerForyear1 = (e) => {
    // useNewOption(newOption);
    setEnteredYear1(e.target.selectedIndex.value);
    console.log(enterdYear1);
  };
  const onChangeHandlerForyear2 = (e) => {
    // useNewOption(newOption);
    setEnteredYear2(e.target.selectedIndex.value);
    console.log(enterdYear2);
  };
  const onChangeHandlerForyear3 = (e) => {
    // useNewOption(newOption);
    setEnteredYea3(e.target.selectedIndex.value);
    console.log(enterdYear3);
  };
  const onChangeHandlerForyear4 = (e) => {
    // useNewOption(newOption);
    setEnteredYea4(e.target.selectedIndex.value);
    console.log(enterdYear4);
  };
  return (
    <div className="expenses-filter">
      <div className="expenses-filter__control">
        <label>Filter by year</label>
        <select>
          <option value="2022" onChange={onChangeHandlerForyear1}>
            2022
          </option>
          <option value="2021" onChange={onChangeHandlerForyear2}>
            2021
          </option>
          <option value="2020" onChange={onChangeHandlerForyear3}>
            2020
          </option>
          <option value="2019" onChange={onChangeHandlerForyear4}>
            2019
          </option>
        </select>
      </div>
    </div>
  );
};

export default ExpensesFilter;
