import "./NewExpense.css";
import ExpensesForm from "./ExpenseForm";

const NewExpenses = (props) => {
  const saveExpenseDataHandler = (enteredExpenseData) => {
    const expenseData = {
      ...enteredExpenseData,
      id: Math.random().toString(),
    };
    props.onaddExpense(expenseData);
  };

  const form = <ExpensesForm onSaveExpenseData={saveExpenseDataHandler} />;
  const btn = (
    <button type="submit" onSubmit={showFormHandler}>
      Add New Expense
    </button>
  );

  const showFormHandler = (e) => {
    e.preventDefault();

    if (e) {
      return true;
    }
    return false;
  };

  return <div className="new-expense">{btn}</div>;
};

export default NewExpenses;
